import { configureStore, createSlice } from "@reduxjs/toolkit";

const initialCartState = { items: [] };

const cartSlice = createSlice({
  name: "cart",
  initialState: initialCartState,
  reducers: {
    addItem(state, action) {
      const newItem = action.payload; // { title, price }
      const existingItem = state.items.find(item => item.title === newItem.title);

      if (existingItem) {
        existingItem.quantity++;
        existingItem.total += newItem.price;
      } else {
        state.items.push({
          title: newItem.title,
          price: newItem.price,
          quantity: 1,
          total: newItem.price,
        });
      }
    },

    removeItem(state, action) {
      const title = action.payload.title; // nüüd otsime title järgi
      const existingItem = state.items.find(item => item.title === title);

      if (!existingItem) return;

      if (existingItem.quantity === 1) {
        state.items = state.items.filter(item => item.title !== title);
      } else {
        existingItem.quantity--;
        existingItem.total -= existingItem.price;
      }
    },
  },
});

export const cartActions = cartSlice.actions;

const store = configureStore({
  reducer: { cart: cartSlice.reducer },
});

export default store;
